import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ProfileCard.css'; // assuming you create a CSS file for this
import { FaTwitter, FaInstagram } from 'react-icons/fa'; // install if not already
const ProfileCard = ({ profile }) => {
  const navigate = useNavigate();

  const handleNavigate = () => {
    navigate(`/profile/${profile.id}`);
  };

  return (
    <div className="profile-card" onClick={handleNavigate}>
      <div className="profile-img-container">
        <img
          src={profile.image}
          alt={`${profile.name}'s avatar`}
          className="profile-img"
        />
      </div>
      <h3 className="profile-name">{profile.name}</h3>
      <p className="profile-bio">{profile.bio}</p>

      <div className="profile-skills">
        {profile.skills?.map((skill, index) => (
          <span key={index} className="skill-tag">
            {skill}
          </span>
        ))}
      </div>
      // Inside the return, between skills and rating:
<div className="profile-socials">
  {profile.twitter && (
    <a
      href={profile.twitter}
      target="_blank"
      rel="noopener noreferrer"
      className="social-icon"
    >
      <FaTwitter />
    </a>
  )}
  {profile.instagram && (
    <a
      href={profile.instagram}
      target="_blank"
      rel="noopener noreferrer"
      className="social-icon"
    >
      <FaInstagram />
    </a>
  )}
</div>



      <div className="profile-rating">
        {'★'.repeat(profile.rating || 4)}
        {'☆'.repeat(5 - (profile.rating || 4))}
      </div>
    </div>
  );
};

export default ProfileCard;
