import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { deleteAccount } from '../api';
import '../styles/SettingsPage.css';

const SettingsPage = () => {
  const [activeTab, setActiveTab] = useState('personal');
  const [isDeleting, setIsDeleting] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleDeleteAccount = async () => {
    // Simple confirmation prompt
    if (!window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      return;
    }

    try {
      setIsDeleting(true);
      setError('');
      
      // Get user ID from localStorage directly
      let userId = user?.id;
      
      // If user ID is not available in context, try to get it from localStorage
      if (!userId) {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
          try {
            const parsedUser = JSON.parse(storedUser);
            userId = parsedUser.id;
            console.log('Retrieved user ID from localStorage:', userId);
          } catch (e) {
            console.error('Error parsing stored user:', e);
          }
        }
      }
      
      // Fallback to 1 if still no user ID (for development purposes)
      userId = userId || 1;
      console.log('Using user ID for deletion:', userId);
      
      // Call the API to delete the account using the user ID
      await deleteAccount(userId);
      
      // If successful, logout and redirect to homepage
      alert('Your account has been deleted successfully.');
      logout();
      navigate('/');
    } catch (err) {
      console.error('Delete account error:', err);
      setError(err.message || 'Failed to delete account. Please try again.');
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="settings-container">
      <h2>Settings</h2>
      <div className="settings-content">

        {/* Tabs - Left Navigation */}
        <div className="tabs">
          <button
            className={activeTab === 'personal' ? 'active' : ''}
            onClick={() => setActiveTab('personal')}
          >
            Personal Info
          </button>
          <button
            className={activeTab === 'account' ? 'active' : ''}
            onClick={() => setActiveTab('account')}
          >
            Account Info
          </button>
          <button
            className={activeTab === 'delete-account' ? 'active' : ''}
            onClick={() => setActiveTab('delete-account')}
          >
            Delete Account
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'personal' && (
            <div className="tab-panel">
              <h3>Personal Information</h3>
              <label>Name</label>
              <input type="text" placeholder="Your Name" defaultValue={user?.name || ''} />
              <label>Email</label>
              <input type="email" placeholder="your@email.com" defaultValue={user?.email || ''} />
              <label>Phone</label>
              <input type="tel" placeholder="+91 1234567890" />
            </div>
          )}

          {activeTab === 'account' && (
            <div className="tab-panel">
              <h3>Account Settings</h3>
              <label>Username</label>
              <input type="text" placeholder="username123" defaultValue={user?.name || ''} />
              <label>Password</label>
              <input type="password" placeholder="••••••••" />
            </div>
          )}

          {activeTab === 'delete-account' && (
            <div className="tab-panel">
              <h3>Delete Account</h3>
              <p>Warning: This action cannot be undone. All your data will be permanently deleted.</p>
              
              {error && (
                <div className="error-message" style={{ color: 'red', marginBottom: '15px' }}>
                  {error}
                </div>
              )}
              
              <button 
                className="delete-account-btn" 
                onClick={handleDeleteAccount}
                disabled={isDeleting}
                style={{ 
                  backgroundColor: '#e74c3c', 
                  color: 'white',
                  padding: '10px 15px',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: isDeleting ? 'not-allowed' : 'pointer',
                  marginTop: '20px'
                }}
              >
                {isDeleting ? 'Deleting...' : 'Delete My Account'}
              </button>
            </div>
          )}
        </div>

      </div>
    </div>
  );
};

export default SettingsPage;
