import React, { useState } from 'react';
import { login as loginAPI } from '../api';
import { useNavigate, Link } from 'react-router-dom';
import '../styles/Login.css';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const { login } = useAuth(); // ðŸ‘ˆ
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await loginAPI(form); // Rename imported `login` to `loginAPI` to avoid conflict
      login(res.data.token); // ðŸ‘ˆ Update auth context
      navigate('/dashboard');
    } catch (err) {
      const errorMsg = err?.response?.data?.msg || 'Login failed. Please try again.';
      setError(errorMsg);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input name="email" placeholder="Email" onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        <button type="submit">Login</button>
      </form>

      <div style={{ marginTop: '10px' }}>
        <Link to="/forgot-password">Forgot Password?</Link>
      </div>

      <div style={{ marginTop: '20px' }}>
        <span>New to Skillshare? </span>
        <Link to="/register">Sign up</Link>
      </div>
    </div>
  );
};

export default Login;
