// client/src/api.js
import axios from 'axios';

const API_BASE = 'http://localhost:5000/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add request interceptor to include auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
      console.log('Adding auth token to request');
    } else {
      console.log('No auth token found in localStorage');
    }
    return config;
  },
  (error) => {
    console.error('Request interceptor error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for debugging
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error Response:', error.response ? {
      status: error.response.status,
      data: error.response.data,
      headers: error.response.headers
    } : 'No response from server');
    return Promise.reject(error);
  }
);

export const register = async (form) => {
  try {
    const res = await api.post('/register', {
      name: form.name,
      email: form.email,
      password: form.password,
      confirmPassword: form.confirmPassword
    });
    return res;
  } catch (err) {
    if (err.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      throw new Error(err.response.data.msg || 'Registration failed');
    } else if (err.request) {
      // The request was made but no response was received
      throw new Error('No response from server. Please check your connection.');
    } else {
      // Something happened in setting up the request that triggered an Error
      throw new Error('Error setting up request');
    }
  }
};

export const login = async (form) => {
  try {
    const res = await api.post('/login', {
      email: form.email,
      password: form.password
    });
    return res;
  } catch (err) {
    if (err.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      throw new Error(err.response.data.msg || 'Login failed');
    } else if (err.request) {
      // The request was made but no response was received
      throw new Error('No response from server. Please check your connection.');
    } else {
      // Something happened in setting up the request that triggered an Error
      throw new Error('Error setting up request');
    }
  }
};

export const deleteAccount = async () => {
  try {
    console.log('Attempting to delete account...');
    // Log token for debugging
    const token = localStorage.getItem('authToken');
    console.log('Auth token exists:', !!token);

    const res = await api.delete('/users/delete-account');
    console.log('Delete account response:', res.data);
    return res;
  } catch (err) {
    console.error('Delete account error details:', err);
    if (err.response) {
      console.error('Server response error:', err.response.status, err.response.data);
      throw new Error(err.response.data.msg || 'Failed to delete account');
    } else if (err.request) {
      console.error('No server response received');
      throw new Error('No response from server. Please check your connection.');
    } else {
      console.error('Request setup error:', err.message);
      throw new Error('Error setting up request');
    }
  }
};
