import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { profileApi } from '../services/api';
import '../styles/AdminDashboard.css';

function AdminDashboard() {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    fullBio: '',
    image: '',
    bannerImage: '',
    popularWork: '',
    workLink: '',
    twitter: '',
    instagram: '',
    positionTitle: '',
    companyName: '',
    experienceSummary: '',
    projectDescription: '',
    githubLink: '',
    skills: '',
    price: 85
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const loadProfile = async () => {
      if (!user?.id) return;

      try {
        const profile = await profileApi.getProfile(user.id);
        setFormData({
          ...profile,
          skills: Array.isArray(profile.skills) ? profile.skills.join(', ') : profile.skills
        });
      } catch (err) {
        console.error('Error loading profile:', err);
        // If profile doesn't exist, use user's email
        setFormData(prev => ({
          ...prev,
          email: user.email
        }));
      }
    };

    loadProfile();
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (success) setSuccess(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      // Prepare the profile data
      const profileData = {
        ...formData,
        skills: formData.skills.split(',').map(skill => skill.trim()).filter(skill => skill),
        price: parseInt(formData.price) || 85,
        rating: 4.5 // Default rating
      };

      let response;
      if (user?.id) {
        // Update existing profile
        response = await profileApi.updateProfile(user.id, profileData);
      } else {
        // Create new profile
        response = await profileApi.createProfile(profileData);
      }

      // Store the updated profile in localStorage for immediate access
      localStorage.setItem('userProfile', JSON.stringify(response));

      setSuccess(true);
      window.scrollTo(0, 0);

      // Redirect to profile page after successful update
      setTimeout(() => {
        navigate(`/profile/${user.id}`);
      }, 2000);
    } catch (err) {
      setError(err.message || 'An error occurred while saving the profile');
      console.error('Error saving profile:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleViewProfile = () => {
    navigate(`/profile/${user.id}`);
  };

  return (
    <div className="admin-dashboard container">
      <h2 className="dashboard-title">My Profile Dashboard</h2>

      {success && (
        <div className="success-message">
          <p>Profile updated successfully! Redirecting to your profile...</p>
        </div>
      )}

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        {/* Basic Information */}
        <div className="section">
          <h4>Basic Information</h4>
          <div className="form-group">
            <label>Name</label>
            <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Short Bio (1-2 sentences)</label>
            <textarea className="form-control" name="bio" value={formData.bio} onChange={handleChange} rows="2" />
          </div>
          <div className="form-group">
            <label>Full Bio</label>
            <textarea className="form-control" name="fullBio" value={formData.fullBio} onChange={handleChange} rows="4" />
          </div>
        </div>

        {/* Images */}
        <div className="section">
          <h4>Images</h4>
          <div className="form-group">
            <label>Profile Image URL</label>
            <input type="text" className="form-control" name="image" value={formData.image} onChange={handleChange} />
            {formData.image && (
              <div className="image-preview">
                <img src={formData.image} alt="Profile preview" />
              </div>
            )}
          </div>
          <div className="form-group">
            <label>Banner Image URL</label>
            <input type="text" className="form-control" name="bannerImage" value={formData.bannerImage} onChange={handleChange} />
            {formData.bannerImage && (
              <div className="banner-preview">
                <img src={formData.bannerImage} alt="Banner preview" />
              </div>
            )}
          </div>
        </div>

        {/* Social Links */}
        <div className="section">
          <h4>Social Links</h4>
          <div className="form-group">
            <label>Popular Work</label>
            <input type="text" className="form-control" name="popularWork" value={formData.popularWork} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Work Link</label>
            <input type="text" className="form-control" name="workLink" value={formData.workLink} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Twitter</label>
            <input type="text" className="form-control" name="twitter" value={formData.twitter} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Instagram</label>
            <input type="text" className="form-control" name="instagram" value={formData.instagram} onChange={handleChange} />
          </div>
        </div>

        {/* Work Experience */}
        <div className="section">
          <h4>Work Experience</h4>
          <div className="form-group">
            <label>Position Title</label>
            <input type="text" className="form-control" name="positionTitle" value={formData.positionTitle} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Company Name</label>
            <input type="text" className="form-control" name="companyName" value={formData.companyName} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Experience Summary</label>
            <textarea className="form-control" name="experienceSummary" value={formData.experienceSummary} onChange={handleChange} rows="3" />
          </div>
        </div>

        {/* Project Details */}
        <div className="section">
          <h4>Project Details</h4>
          <div className="form-group">
            <label>Project Description</label>
            <textarea className="form-control" name="projectDescription" value={formData.projectDescription} onChange={handleChange} rows="3" />
          </div>
          <div className="form-group">
            <label>GitHub Link</label>
            <input type="text" className="form-control" name="githubLink" value={formData.githubLink} onChange={handleChange} />
          </div>
        </div>

        {/* Skills */}
        <div className="section">
          <h4>Skills</h4>
          <div className="form-group">
            <label>Skills (comma-separated)</label>
            <textarea 
              className="form-control" 
              name="skills" 
              value={formData.skills} 
              onChange={handleChange} 
              rows="2"
              placeholder="React, JavaScript, CSS, UI Design, etc."
            />
          </div>
          <div className="form-group">
            <label>Hourly Rate ($)</label>
            <input 
              type="number" 
              className="form-control" 
              name="price" 
              value={formData.price} 
              onChange={handleChange}
              min="0"
            />
          </div>
        </div>

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn btn-primary" 
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Profile'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default AdminDashboard;
