// src/pages/Profile.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { profileApi } from '../services/api';
import { FaTwitter, FaInstagram, FaGithub, FaExternalLinkAlt, FaStar, FaEdit, FaDollarSign } from 'react-icons/fa';
import '../styles/Profile.css';
import defaultCoverImage from '../assets/default-cover.jpg';
import defaultProfileImage from '../assets/default-profile.jpg';

const Profile = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProfile = async () => {
      try {
        if (!user) {
          navigate('/login');
          return;
        }

        const data = await profileApi.getProfile(user.id);
        setProfile(data);
      } catch (err) {
        setError('Failed to load profile');
        console.error('Error loading profile:', err);
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [user, navigate]);

  const handleImageError = (e, type) => {
    e.target.onerror = null; // Prevent infinite loop
    e.target.src = type === 'cover' ? defaultCoverImage : defaultProfileImage;
  };

  const handleEditProfile = () => {
    navigate('/admin-dashboard');
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button onClick={() => navigate('/admin')}>Create Profile</button>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="no-profile">
        <h2>No Profile Found</h2>
        <p>Please create your profile to get started.</p>
        <button onClick={() => navigate('/admin')}>Create Profile</button>
      </div>
    );
  }

  // Parse skills if they're stored as a JSON string
  const skills = typeof profile.skills === 'string' 
    ? JSON.parse(profile.skills) 
    : profile.skills || [];

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="cover-photo">
          <img 
            src={profile.coverPhoto || defaultCoverImage} 
            alt="Cover" 
            onError={(e) => handleImageError(e, 'cover')}
          />
        </div>
        <div className="profile-info">
          <div className="profile-photo">
            <img 
              src={profile.profilePhoto || defaultProfileImage} 
              alt={profile.name} 
              onError={(e) => handleImageError(e, 'profile')}
            />
          </div>
          <div className="profile-details">
            <h1>{profile.name}</h1>
            <p className="bio">{profile.bio}</p>
            <div className="social-links">
              {profile.linkedin && (
                <a href={profile.linkedin} target="_blank" rel="noopener noreferrer">
                  LinkedIn
                </a>
              )}
              {profile.github && (
                <a href={profile.github} target="_blank" rel="noopener noreferrer">
                  GitHub
                </a>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="section">
          <h2>Work Experience</h2>
          {profile.workExperience && profile.workExperience.length > 0 ? (
            <div className="experience-list">
              {profile.workExperience.map((exp, index) => (
                <div key={index} className="experience-item">
                  <h3>{exp.company}</h3>
                  <p className="position">{exp.position}</p>
                  <p className="duration">{exp.duration}</p>
                  <p className="description">{exp.description}</p>
                </div>
              ))}
            </div>
          ) : (
            <p>No work experience added yet.</p>
          )}
        </div>

        <div className="section">
          <h2>Projects</h2>
          {profile.projects && profile.projects.length > 0 ? (
            <div className="projects-list">
              {profile.projects.map((project, index) => (
                <div key={index} className="project-item">
                  <h3>{project.title}</h3>
                  <p className="description">{project.description}</p>
                  {project.link && (
                    <a href={project.link} target="_blank" rel="noopener noreferrer">
                      View Project
                    </a>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <p>No projects added yet.</p>
          )}
        </div>

        <div className="section">
          <h2>Skills</h2>
          {profile.skills && profile.skills.length > 0 ? (
            <div className="skills-list">
              {profile.skills.map((skill, index) => (
                <div key={index} className="skill-item">
                  <span className="skill-name">{skill.name}</span>
                  <div className="skill-rating">
                    {[...Array(5)].map((_, i) => (
                      <span
                        key={i}
                        className={`star ${i < skill.rating ? 'filled' : ''}`}
                      >
                        â˜…
                      </span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>No skills added yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
