import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { updateProfile, getDashboardData, getSkills, getProjects } from '../api';
import '../styles/AdminDashboard.css';

function AdminDashboard() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    bio: '',
    fullBio: '',
    image: '',
    bannerImage: '',
    popularWork: '',
    workLink: '',
    twitter: '',
    instagram: '',
    positionTitle: '',
    companyName: '',
    experienceSummary: '',
    projectDescription: '',
    githubLink: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dashboardData, setDashboardData] = useState(null);
  const [skills, setSkills] = useState([]);
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [dashboard, skillsData, projectsData] = await Promise.all([
          getDashboardData(),
          getSkills(),
          getProjects()
        ]);
        setDashboardData(dashboard);
        setSkills(skillsData);
        setProjects(projectsData);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        throw new Error('User not authenticated');
      }

      await updateProfile(userId, formData);
      navigate(`/profile/${userId}`);
    } catch (err) {
      setError(err.message || 'An error occurred while saving the profile');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      
      {dashboardData && (
        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>Total Users</h3>
            <p>{dashboardData.totalUsers}</p>
          </div>
          <div className="stat-card">
            <h3>Total Profiles</h3>
            <p>{dashboardData.totalProfiles}</p>
          </div>
          <div className="stat-card">
            <h3>Total Skills</h3>
            <p>{dashboardData.totalSkills}</p>
          </div>
          <div className="stat-card">
            <h3>Total Projects</h3>
            <p>{dashboardData.totalProjects}</p>
          </div>
        </div>
      )}

      <div className="profile-form">
        <h3>Edit Profile</h3>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Bio</label>
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Short bio"
            />
          </div>

          <div className="form-group">
            <label>Full Bio</label>
            <textarea
              name="fullBio"
              value={formData.fullBio}
              onChange={handleChange}
              placeholder="Detailed bio"
            />
          </div>

          <div className="form-group">
            <label>Position Title</label>
            <input
              type="text"
              name="positionTitle"
              value={formData.positionTitle}
              onChange={handleChange}
              placeholder="Your position"
            />
          </div>

          <div className="form-group">
            <label>Company Name</label>
            <input
              type="text"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
              placeholder="Your company"
            />
          </div>

          <div className="form-group">
            <label>Experience Summary</label>
            <textarea
              name="experienceSummary"
              value={formData.experienceSummary}
              onChange={handleChange}
              placeholder="Your experience"
            />
          </div>

          <div className="form-group">
            <label>Social Links</label>
            <input
              type="text"
              name="twitter"
              value={formData.twitter}
              onChange={handleChange}
              placeholder="Twitter URL"
            />
            <input
              type="text"
              name="instagram"
              value={formData.instagram}
              onChange={handleChange}
              placeholder="Instagram URL"
            />
            <input
              type="text"
              name="githubLink"
              value={formData.githubLink}
              onChange={handleChange}
              placeholder="GitHub URL"
            />
          </div>

          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Profile'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default AdminDashboard;
