import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaEnvelope, FaPhone, FaMapMarkerAlt, FaLinkedin, FaGithub, FaGlobe, FaCalendarAlt } from 'react-icons/fa';
import { profileApi } from '../services/api';
import '../styles/ProfileDetail.css';

const ProfileDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const data = await profileApi.getProfile(id);
        setProfile(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load profile. Please try again later.');
        setLoading(false);
      }
    };

    fetchProfile();
  }, [id]);

  const handleMessageClick = () => {
    navigate(`/messages/${id}`);
  };

  if (loading) {
    return (
      <div className="profile-detail-loading">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-detail-error">
        <p>{error}</p>
        <button onClick={() => navigate(-1)}>Go Back</button>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="profile-detail-error">
        <p>Profile not found</p>
        <button onClick={() => navigate(-1)}>Go Back</button>
      </div>
    );
  }

  return (
    <div className="profile-detail-container">
      <div 
        className="profile-detail-header"
        style={{ backgroundImage: `url(${profile.bannerImage || 'https://images.unsplash.com/photo-1579546929518-9e396f3cc809'})` }}
      >
        <button className="profile-detail-message-button" onClick={handleMessageClick}>
          <FaEnvelope /> Send Message
        </button>
        <div className="profile-detail-image-container">
          {profile.profileImage ? (
            <img 
              src={profile.profileImage} 
              alt={profile.name} 
              className="profile-detail-image"
            />
          ) : (
            <div className="profile-detail-image-placeholder">
              {profile.name.charAt(0).toUpperCase()}
            </div>
          )}
        </div>
      </div>

      <div className="profile-detail-content">
        <h1 className="profile-detail-name">{profile.name}</h1>
        <p className="profile-detail-position">{profile.position}</p>
        <p className="profile-detail-bio">{profile.bio}</p>

        <div className="profile-detail-section">
          <h2 className="profile-detail-section-title">Contact Information</h2>
          <div className="profile-detail-contact-info">
            {profile.email && (
              <div className="profile-detail-contact-item">
                <FaEnvelope />
                <span>{profile.email}</span>
              </div>
            )}
            {profile.phone && (
              <div className="profile-detail-contact-item">
                <FaPhone />
                <span>{profile.phone}</span>
              </div>
            )}
            {profile.location && (
              <div className="profile-detail-contact-item">
                <FaMapMarkerAlt />
                <span>{profile.location}</span>
              </div>
            )}
          </div>

          <div className="profile-detail-social-links">
            {profile.linkedin && (
              <a href={profile.linkedin} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaLinkedin /> LinkedIn
              </a>
            )}
            {profile.github && (
              <a href={profile.github} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaGithub /> GitHub
              </a>
            )}
            {profile.website && (
              <a href={profile.website} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaGlobe /> Website
              </a>
            )}
          </div>
        </div>

        {profile.experience && profile.experience.length > 0 && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Experience</h2>
            <div className="profile-detail-experience-list">
              {profile.experience.map((exp, index) => (
                <div key={index} className="profile-detail-experience-item">
                  <h3 className="profile-detail-experience-title">{exp.title}</h3>
                  <p className="profile-detail-experience-company">{exp.company}</p>
                  <div className="profile-detail-experience-date">
                    <FaCalendarAlt />
                    <span>{exp.startDate} - {exp.endDate || 'Present'}</span>
                  </div>
                  <p className="profile-detail-experience-description">{exp.description}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {profile.projects && profile.projects.length > 0 && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Projects</h2>
            <div className="profile-detail-projects-grid">
              {profile.projects.map((project, index) => (
                <div key={index} className="profile-detail-project-card">
                  <h3 className="profile-detail-project-title">{project.title}</h3>
                  <p className="profile-detail-project-description">{project.description}</p>
                  <div className="profile-detail-project-tags">
                    {project.technologies.map((tech, techIndex) => (
                      <span key={techIndex} className="profile-detail-project-tag">
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileDetail;
