import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { profileApi } from '../services/api';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    positionTitle: '',
    companyName: '',
    bio: '',
    fullBio: '',
    image: '',
    bannerImage: '',
    twitter: '',
    instagram: '',
    githubLink: '',
    workLink: '',
    experienceSummary: '',
    popularWork: '',
    projectDescription: '',
    skills: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [newSkill, setNewSkill] = useState('');

  useEffect(() => {
    const loadProfile = async () => {
      if (!user?.id) {
        setError('User not authenticated');
        setLoading(false);
        return;
      }

      try {
        const data = await profileApi.getProfile(user.id);
        if (data) {
          // Ensure skills is always an array
          const profileData = {
            ...data,
            skills: Array.isArray(data.skills) ? data.skills : 
                   typeof data.skills === 'string' ? JSON.parse(data.skills) : []
          };
          setFormData(profileData);
        }
      } catch (err) {
        console.error('Error loading profile:', err);
        setError(err.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSkillAdd = () => {
    if (newSkill.trim()) {
      setFormData(prev => ({
        ...prev,
        skills: [...prev.skills, newSkill.trim()]
      }));
      setNewSkill('');
    }
  };

  const handleSkillRemove = (index) => {
    setFormData(prev => ({
      ...prev,
      skills: prev.skills.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);
    setLoading(true);

    try {
      // Prepare the profile data
      const profileData = {
        ...formData,
        userId: user.id,
        skills: JSON.stringify(formData.skills),
        rating: formData.rating || 4.5,
        price: formData.price || 85
      };

      let response;
      if (formData.id) {
        // Update existing profile
        response = await profileApi.updateProfile(user.id, profileData);
      } else {
        // Create new profile
        response = await profileApi.createProfile(profileData);
      }

      // Store the updated profile in localStorage
      localStorage.setItem('userProfile', JSON.stringify(response));

      setSuccess('Profile updated successfully!');
      setTimeout(() => {
        navigate('/profile');
      }, 1500);
    } catch (err) {
      console.error('Error saving profile:', err);
      setError(err.message || 'Failed to save profile');
    } finally {
      setLoading(false);
    }
  };

  if (loading && !formData.id) {
    return (
      <div className="admin-dashboard-loading">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <h1>Edit Profile</h1>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-section">
          <h2>Basic Information</h2>

          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Position Title:</label>
            <input
              type="text"
              name="positionTitle"
              value={formData.positionTitle}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Company Name:</label>
            <input
              type="text"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Images</h2>

          <div className="form-group">
            <label>Profile Image URL:</label>
            <input
              type="url"
              name="image"
              value={formData.image}
              onChange={handleChange}
              placeholder="https://example.com/image.jpg"
            />
          </div>

          <div className="form-group">
            <label>Banner Image URL:</label>
            <input
              type="url"
              name="bannerImage"
              value={formData.bannerImage}
              onChange={handleChange}
              placeholder="https://example.com/banner.jpg"
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Social Links</h2>

          <div className="form-group">
            <label>Twitter:</label>
            <input
              type="url"
              name="twitter"
              value={formData.twitter}
              onChange={handleChange}
              placeholder="https://twitter.com/username"
            />
          </div>

          <div className="form-group">
            <label>Instagram:</label>
            <input
              type="url"
              name="instagram"
              value={formData.instagram}
              onChange={handleChange}
              placeholder="https://instagram.com/username"
            />
          </div>

          <div className="form-group">
            <label>GitHub:</label>
            <input
              type="url"
              name="githubLink"
              value={formData.githubLink}
              onChange={handleChange}
              placeholder="https://github.com/username"
            />
          </div>

          <div className="form-group">
            <label>Work Link:</label>
            <input
              type="url"
              name="workLink"
              value={formData.workLink}
              onChange={handleChange}
              placeholder="https://example.com/work"
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Work Experience</h2>

          <div className="form-group">
            <label>Experience Summary:</label>
            <textarea
              name="experienceSummary"
              value={formData.experienceSummary}
              onChange={handleChange}
              rows="4"
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Project Details</h2>

          <div className="form-group">
            <label>Popular Work:</label>
            <input
              type="text"
              name="popularWork"
              value={formData.popularWork}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Project Description:</label>
            <textarea
              name="projectDescription"
              value={formData.projectDescription}
              onChange={handleChange}
              rows="4"
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Skills</h2>

          <div className="skills-input">
            <input
              type="text"
              value={newSkill}
              onChange={(e) => setNewSkill(e.target.value)}
              placeholder="Add a skill"
            />
            <button type="button" onClick={handleSkillAdd}>Add</button>
          </div>

          <div className="skills-list">
            {formData.skills.map((skill, index) => (
              <div key={index} className="skill-tag">
                {skill}
                <button
                  type="button"
                  onClick={() => handleSkillRemove(index)}
                  className="remove-skill"
                >
                  Ã—
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="form-section">
          <h2>Bio</h2>

          <div className="form-group">
            <label>Short Bio:</label>
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              rows="3"
              required
            />
          </div>

          <div className="form-group">
            <label>Full Bio:</label>
            <textarea
              name="fullBio"
              value={formData.fullBio}
              onChange={handleChange}
              rows="6"
              required
            />
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Saving...' : 'Save Profile'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AdminDashboard;
