import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaEnvelope, FaBuilding, FaGithub, FaInstagram, FaTwitter, FaLink, FaStar, FaRupeeSign } from 'react-icons/fa';
import { api } from '../services/api';
import '../styles/ProfileDetail.css';

const ProfileDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await api.get(`/api/profiles/${id}`);
        setProfile(response);
        setLoading(false);
      } catch (err) {
        setError('Failed to load profile');
        setLoading(false);
      }
    };

    fetchProfile();
  }, [id]);

  if (loading) {
    return (
      <div className="profile-detail-loading">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-detail-error">
        <p>{error}</p>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="profile-detail-error">
        <p>Profile not found</p>
      </div>
    );
  }

  return (
    <div className="profile-detail-container">
      <div 
        className="profile-detail-header"
        style={{ backgroundImage: profile.bannerImage ? `url(${profile.bannerImage})` : 'none' }}
      >
        <div className="profile-detail-image-container">
          {profile.profileImage ? (
            <img 
              src={profile.profileImage} 
              alt={profile.name} 
              className="profile-detail-image"
            />
          ) : (
            <div className="profile-detail-image-placeholder">
              {profile.name?.charAt(0)}
            </div>
          )}
        </div>
        <button 
          className="profile-detail-message-button"
          onClick={() => navigate(`/chat/${profile.id}`)}
        >
          Message
        </button>
      </div>

      <div className="profile-detail-content">
        <h1 className="profile-detail-name">{profile.name}</h1>
        {profile.positionTitle && (
          <p className="profile-detail-position">{profile.positionTitle}</p>
        )}
        {profile.bio && (
          <p className="profile-detail-bio">{profile.bio}</p>
        )}

        <div className="profile-detail-section">
          <h2 className="profile-detail-section-title">Contact Information</h2>
          <div className="profile-detail-contact-info">
            {profile.email && (
              <div className="profile-detail-contact-item">
                <FaEnvelope />
                <span>{profile.email}</span>
              </div>
            )}
            {profile.companyName && (
              <div className="profile-detail-contact-item">
                <FaBuilding />
                <span>{profile.companyName}</span>
              </div>
            )}
          </div>
        </div>

        <div className="profile-detail-section">
          <h2 className="profile-detail-section-title">Social Links</h2>
          <div className="profile-detail-social-links">
            {profile.githubLink && (
              <a href={profile.githubLink} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaGithub />
                GitHub
              </a>
            )}
            {profile.instagramLink && (
              <a href={profile.instagramLink} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaInstagram />
                Instagram
              </a>
            )}
            {profile.twitterLink && (
              <a href={profile.twitterLink} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaTwitter />
                Twitter
              </a>
            )}
            {profile.portfolioLink && (
              <a href={profile.portfolioLink} target="_blank" rel="noopener noreferrer" className="profile-detail-social-link">
                <FaLink />
                Portfolio
              </a>
            )}
          </div>
        </div>

        {profile.fullBio && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">About</h2>
            <p className="profile-detail-full-bio">{profile.fullBio}</p>
          </div>
        )}

        {profile.experienceSummary && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Experience</h2>
            <p className="profile-detail-experience-summary">{profile.experienceSummary}</p>
          </div>
        )}

        {profile.popularWork && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Popular Work</h2>
            <p className="profile-detail-popular-work">{profile.popularWork}</p>
          </div>
        )}

        {profile.projectDescription && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Project Description</h2>
            <p className="profile-detail-project-description">{profile.projectDescription}</p>
          </div>
        )}

        {profile.skills && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Skills</h2>
            <div className="profile-detail-skills-list">
              {(() => {
                try {
                  const skillsArray = typeof profile.skills === 'string' 
                    ? JSON.parse(profile.skills) 
                    : profile.skills;
                  return skillsArray.map((skill, index) => (
                    <span key={index} className="profile-detail-skill-tag">
                      {skill}
                    </span>
                  ));
                } catch (error) {
                  return <span className="profile-detail-skill-tag">{profile.skills}</span>;
                }
              })()}
            </div>
          </div>
        )}

        {(profile.pricePerHour || profile.rating) && (
          <div className="profile-detail-section">
            <h2 className="profile-detail-section-title">Pricing & Rating</h2>
            <div className="profile-detail-pricing-info">
              {profile.pricePerHour && (
                <div className="profile-detail-price">
                  <FaRupeeSign />
                  <span className="profile-detail-price-label">Price per hour:</span>
                  <span className="profile-detail-price-value">{profile.pricePerHour}</span>
                </div>
              )}
              {profile.rating && (
                <div className="profile-detail-rating">
                  <FaStar />
                  <span className="profile-detail-rating-label">Rating:</span>
                  <span className="profile-detail-rating-value">{profile.rating}</span>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileDetail;
