import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const MessagingLanding = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() !== '') {
      setMessages([...messages, { text: input, sender: 'You' }]);
      setInput('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const styles = {
    page: {
      minHeight: '100vh',
      backgroundColor: '#1e1e2f',
      color: '#fff',
      fontFamily: 'Segoe UI, sans-serif',
      padding: '20px',
    },
    navbar: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '30px',
    },
    navLinks: {
      display: 'flex',
      gap: '20px',
      alignItems: 'center',
    },
    logo: {
      fontWeight: 'bold',
      fontSize: '28px',
      color: '#6c5ce7',
    },
    btnPrimary: {
      backgroundColor: '#ff6b81',
      border: 'none',
      padding: '8px 20px',
      borderRadius: '20px',
      color: '#fff',
    },
    chatBox: {
      backgroundColor: '#2f2f3f',
      padding: '20px',
      borderRadius: '10px',
      height: '400px',
      overflowY: 'auto',
      marginBottom: '10px',
    },
    message: {
      marginBottom: '10px',
      padding: '10px',
      borderRadius: '10px',
      backgroundColor: '#3e3e50',
    },
    inputArea: {
      display: 'flex',
      gap: '10px',
    },
    input: {
      flexGrow: 1,
    },
  };

  return (
    <div style={styles.page}>
      {/* Navbar */}
      <header style={styles.navbar}>
        <div style={styles.logo}>YOUR <span style={{ color: '#fff' }}>WEBSITE</span></div>
        <div style={styles.navLinks}>
          <a className="text-light" href="#">Home</a>
          <a className="text-light" href="#">About</a>
          <a className="text-light" href="#">Work</a>
          <a className="text-light" href="#">Info</a>
          <button style={styles.btnPrimary}>Get Started</button>
        </div>
      </header>

      {/* Chat Section */}
      <section className="container">
        <h2>Live Chat</h2>
        <div style={styles.chatBox}>
          {messages.map((msg, index) => (
            <div key={index} style={styles.message}>
              <strong>{msg.sender}:</strong> {msg.text}
            </div>
          ))}
        </div>

        <div style={styles.inputArea}>
          <input
            type="text"
            className="form-control"
            style={styles.input}
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
          />
          <button className="btn btn-primary" onClick={handleSend}>Send</button>
        </div>
      </section>
    </div>
  );
};

export default MessagingLanding;
