import React, { useState } from 'react';
import '../styles/MessagingLanding.css';

const dummyUsers = ['Alice', 'Bob', 'Charlie', 'Recruiter'];

const MessagingLanding = () => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [conversations, setConversations] = useState({});
  const [input, setInput] = useState('');

  const handleUserSelect = (user) => {
    setSelectedUser(user);
    if (!conversations[user]) {
      setConversations((prev) => ({ ...prev, [user]: [] }));
    }
  };

  const handleSend = () => {
    if (input.trim() && selectedUser) {
      const newMsg = { sender: 'You', text: input };
      setConversations((prev) => ({
        ...prev,
        [selectedUser]: [...prev[selectedUser], newMsg],
      }));
      setInput('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="messaging-page">
      {/* Sidebar with users */}
      <aside className="sidebar">
        <h4>Contacts</h4>
        <ul>
          {dummyUsers.map((user) => (
            <li
              key={user}
              className={selectedUser === user ? 'active' : ''}
              onClick={() => handleUserSelect(user)}
            >
              {user}
            </li>
          ))}
        </ul>
      </aside>

      {/* Main Chat Window */}
      <main className="chat-section">
        {selectedUser ? (
          <>
            <div className="chat-header">
              <h5>Chatting with {selectedUser}</h5>
            </div>
            <div className="chat-box">
              {(conversations[selectedUser] || []).map((msg, i) => (
                <div key={i} className="message">
                  <strong>{msg.sender}:</strong> {msg.text}
                </div>
              ))}
            </div>
            <div className="input-area">
              <input
                type="text"
                className="form-control"
                placeholder="Type a message..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyPress}
              />
              <button className="btn btn-primary" onClick={handleSend}>
                Send
              </button>
            </div>
          </>
        ) : (
          <div className="placeholder">Select a contact to start chatting</div>
        )}
      </main>
    </div>
  );
};

export default MessagingLanding;
