import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ProfileCard.css';

const ProfileCard = ({ profile }) => {
  const navigate = useNavigate();
  const [showHoverBox, setShowHoverBox] = useState(false);

  const handleViewProfile = () => {
    navigate('/profile-detail', { state: { profile } });
  };

  const handleContactClick = (e) => {
    e.stopPropagation();
    alert(`Getting in touch with ${profile.name}...`);
  };

  return (
    <div className="custom-card" onClick={handleViewProfile}>
      <div
        className="banner"
        style={{ backgroundImage: `url(${profile.bannerImage})` }}
      >
        {showHoverBox && (
          <div
            className="hover-box"
            onMouseEnter={() => setShowHoverBox(true)}
            onMouseLeave={() => setShowHoverBox(false)}
          >
            <p><strong>{profile.name}</strong></p>
            <p>{profile.country}</p>
            <p>‚≠ê {profile.rating}</p>
            <button onClick={handleContactClick}>Get in Touch</button>
          </div>
        )}
      </div>

      <div className="profile-info">
        <img src={profile.image} alt={profile.name} className="profile-photo" />
        <h3
          onMouseEnter={() => setShowHoverBox(true)}
          onMouseLeave={() => setShowHoverBox(false)}
        >
          {profile.name}
        </h3>
      </div>
    </div>
  );
};

export default ProfileCard;
