import React, { useState } from 'react';
import ProfileCard from './ProfileCard';
import '../styles/User.css';
import { FaSearch, FaFilter } from 'react-icons/fa';
import { dummyProfiles } from '../data/dummyProfiles';

const User = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    skills: [],
    rating: null,
    availability: 'all'
  });

  const filteredProfiles = dummyProfiles.filter((profile) => {
    const matchesSearch = 
      profile.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      profile.bio?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      profile.skills?.some(skill => 
        skill.toLowerCase().includes(searchTerm.toLowerCase())
      );

    const matchesSkills = filters.skills.length === 0 || 
      filters.skills.every(skill => 
        profile.skills?.includes(skill)
      );

    const matchesRating = !filters.rating || 
      (profile.rating >= filters.rating);

    return matchesSearch && matchesSkills && matchesRating;
  });

  return (
    <div className="home-container">
      <h1>Discover Skilled Professionals</h1>
      
      <div className="search-container">
        <div className="search-wrapper">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search by skill, name, or interest..."
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <div className="card-grid">
        {filteredProfiles.length > 0 ? (
          filteredProfiles.map((profile) => (
            <ProfileCard key={profile.id} profile={profile} />
          ))
        ) : (
          <p>No profiles found.</p>
        )}
      </div>
    </div>
  );
};

export default User;
