import React, { useEffect } from "react";

function App() {
  return <AboutUs />;
}

const AboutUs = () => {
  useEffect(() => {
    const style = document.createElement("style");
    style.innerHTML = `
      body {
        margin: 0;
        padding: 0;
        background-color: #121212;
        color: #e0e0e0;
        font-family: 'Segoe UI', sans-serif;
      }

      .about-container {
        padding: 50px 20px;
        max-width: 1200px;
        margin: auto;
        text-align: center;
      }

      .about-title {
        font-size: 3rem;
        margin-bottom: 40px;
        color: #ffffff;
        animation: fadeIn 1s ease-in-out;
      }

      .team-grid {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        gap: 30px;
      }

      .team-card {
        background-color: #1e1e1e;
        border-radius: 16px;
        padding: 25px;
        width: 300px;
        box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
        transition: transform 0.3s ease, box-shadow 0.3s ease;
        animation: fadeInUp 1s ease forwards;
        opacity: 0;
      }

      .team-card:hover {
        transform: translateY(-10px);
        box-shadow: 0 15px 30px rgba(0, 0, 0, 0.5);
      }

      .team-img {
        width: 100%;
        height: 250px;
        object-fit: cover;
        border-radius: 12px;
      }

      .team-name {
        font-size: 1.5rem;
        margin: 15px 0 5px;
        color: #ffffff;
      }

      .team-role {
        font-size: 1rem;
        font-weight: 600;
        color: #bbbbbb;
        margin-bottom: 10px;
      }

      .team-desc {
        font-size: 0.95rem;
        color: #cccccc;
      }

      @keyframes fadeInUp {
        from {
          transform: translateY(20px);
          opacity: 0;
        }
        to {
          transform: translateY(0);
          opacity: 1;
        }
      }

      @keyframes fadeIn {
        from {
          opacity: 0;
          transform: scale(0.95);
        }
        to {
          opacity: 1;
          transform: scale(1);
        }
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  const teamMembers = [
    {
      name: "Tejas Nikumbh",
      role: "Frontend Developer",
      img: "https://via.placeholder.com/300x250?text=Aarav",
      desc: "Tejas is responsible for creating the user interface and ensuring a smooth user experience.",
    },
    {
      name: "Utsav",
      role: "Backend Developer",
      img: "https://via.placeholder.com/300x250?text=Ishita",
      desc: "Utsav handles server-side logic and database integration, ensuring performance and security.",
    },
    {
      name: "Yash",
      role: "Product Manager",
      img: "https://via.placeholder.com/300x250?text=Rohan",
      desc: "Yash ensures that product vision aligns with user needs and oversees timely project delivery.",
    },
  ];

  return (
    <div className="about-container">
      <h1 className="about-title">About Us</h1>
      <div className="team-grid">
        {teamMembers.map((member, index) => (
          <div className="team-card" key={index} style={{ animationDelay: `${index * 0.3}s` }}>
            <img src={member.img} alt={member.name} className="team-img" />
            <h3 className="team-name">{member.name}</h3>
            <p className="team-role">{member.role}</p>
            <p className="team-desc">{member.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
