// src/components/Sidebar.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { FaHome, FaTachometerAlt, FaEnvelope, FaUserCircle } from 'react-icons/fa';
import '../styles/Sidebar.css';

function Sidebar({ isCollapsed, onToggle, user }) {
  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-toggle" onClick={onToggle}>
        â˜°
      </div>

      <div className="sidebar-menu">
        <Link to="/">
          <FaHome />
          {!isCollapsed && <span>Home</span>}
        </Link>
        <Link to="/dashboard">
          <FaTachometerAlt />
          {!isCollapsed && <span>Dashboard</span>}
        </Link>
        <Link to="/messages">
          <FaEnvelope />
          {!isCollapsed && <span>Messages</span>}
        </Link>
        <div className="profile-icon">
          <FaUserCircle />
        </div>
      </div>

      {!isCollapsed && (
        <div className="profile-box">
          <div className="profile-header">
            <img src={user.image} alt="User" className="profile-img" />
            <div className="profile-info">
              <div className="profile-name">{user.name}</div>
              <div className="profile-role">{user.status}</div>
            </div>
          </div>
          <div className="profile-options">
            <button>Switch to Talent</button>
            <button>Settings</button>
            <button>Help</button>
            <button className="logout-btn">Logout</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Sidebar;
