import React, { useState } from 'react';
import {
  FaHome,
  FaTachometerAlt,
  FaEnvelope,
  FaUserCircle,
  FaBars,
  FaCog,
  FaQuestionCircle,
  FaSignOutAlt,
} from 'react-icons/fa';
import { Link } from 'react-router-dom';
import LogoutConfirmationModal from './LogoutConfirmationModal';
import './Sidebar.css';

function Sidebar({ isCollapsed, toggleSidebar }) {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
 

  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    setShowLogoutModal(false);
    // Actual logout logic here
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <button onClick={toggleSidebar} className="toggle-btn">
          <FaBars />
        </button>
      </div>

      {/* Profile at top */}
      <div className="profile-display">
        <FaUserCircle className="profile-icon" />
        {!isCollapsed && (
          <div className="profile-text">
            <div className="profile-name">John Doe</div>
            <div className="profile-email">john@example.com</div>
          </div>
        )}
      </div>

      {/* Navigation Links */}
      <div className="sidebar-links">
        <Link to="/user">
          <FaHome /> {!isCollapsed && <span>Home</span>}
        </Link>
        <Link to="/admin-dashboard">
          <FaTachometerAlt /> {!isCollapsed && <span>Dashboard</span>}
        </Link>
        <Link to="/MessagingLanding">
          <FaEnvelope /> {!isCollapsed && <span>Messages</span>}
        </Link>

        <hr className="sidebar-divider" />

        <Link to="/contact">
          <FaQuestionCircle /> {!isCollapsed && <span>Help</span>}
        </Link>
        <Link to="/settings">
          <FaCog /> {!isCollapsed && <span>Settings</span>}
        </Link>
        <button className="logout-link" onClick={handleLogoutClick}>
          <FaSignOutAlt /> {!isCollapsed && <span>Logout</span>}
        </button>
      </div>

      <LogoutConfirmationModal
        show={showLogoutModal}
        onConfirm={confirmLogout}
        onCancel={cancelLogout}
      />
    </div>
  );
}

export default Sidebar;
