import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {

  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/login');
  };

  useEffect(() => {
    const fadeInElements = document.querySelectorAll('.fade-in');

    const observer = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('fade-in-visible');
          observer.unobserve(entry.target);
        }
      });
    }, {
      threshold: 0.3
    });

    fadeInElements.forEach(element => {
      observer.observe(element);
    });
  }, []);

  return (
    <div>
      {/* Header */}
      <header style={{
        background: '#1e1e2f',
        padding: '1.5rem 5%',
        color: '#fff',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h1 style={{ fontSize: '1.8rem' }}>SkillSharers</h1>
      </header>

      {/* Hero Section */}
      <section style={{
        background: `url('https://images.unsplash.com/photo-1521737604893-d14cc237f11d?ixlib=rb-4.0.3&auto=format&fit=crop&w=1950&q=80') center/cover no-repeat`,
        height: '90vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        color: '#fff',
        padding: '0 5%'
      }}>
        <div>
          <h2 style={{ fontSize: '3rem', marginBottom: '1rem' }}>Learn New Skills from Industry Experts</h2>
          <p style={{ fontSize: '1.2rem', marginBottom: '2rem' }}>Join thousands of learners and mentors from across the globe.</p>
          <button onClick={handleGetStarted} style={{
            padding: '0.8rem 2rem',
            fontSize: '1rem',
            backgroundColor: '#ff7b54',
            border: 'none',
            borderRadius: '5px',
            color: '#fff',
            cursor: 'pointer'
          }}>Get Started</button>
        </div>
      </section>

      {/* About Section */}
      <section className="fade-in" style={{ padding: '4rem 5%' }}>
        <div style={{
          display: 'flex',
          gap: '3rem',
          alignItems: 'center',
          flexWrap: 'wrap'
        }}>
          <img src="https://images.unsplash.com/photo-1573496529574-be85d6a60704?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=60" alt="About Skill Sharing" style={{
            width: '45%',
            borderRadius: '10px'
          }} />
          <div style={{ flex: '1' }}>
            <h2 style={{
              textAlign: 'center',
              marginBottom: '2rem',
              fontSize: '2rem',
              color: '#1e1e2f'
            }}>About Our Website</h2>
            <p>SkillSharers is a modern platform that connects learners and professionals across the globe to share knowledge and grow together. We aim to make learning accessible, flexible, and personal. Whether you're looking to enhance your resume, learn a hobby, or switch careers—SkillSharers offers mentors, courses, and resources to help you succeed. Our platform promotes peer-to-peer collaboration, video sessions, and practical projects for real-world learning.</p>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="fade-in" style={{ padding: '4rem 5%' }}>
        <h2 style={{
          textAlign: 'center',
          marginBottom: '2rem',
          fontSize: '2rem',
          color: '#1e1e2f'
        }}>Popular Categories</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
          gap: '1.5rem',
          textAlign: 'center'
        }}>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>Web Development</div>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>Data Science</div>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>Digital Marketing</div>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>UI/UX Design</div>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>Cybersecurity</div>
          <div style={{
            background: '#fff',
            borderRadius: '10px',
            padding: '2rem 1rem',
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
          }}>Public Speaking</div>
        </div>
      </section>

      {/* Footer */}
      <footer style={{
        backgroundColor: '#101020',
        color: '#ccc',
        textAlign: 'center',
        padding: '2rem 5%'
      }}>
        <p>© 2025 SkillSharers. All rights reserved.</p>
        <p>Developed with ❤️ by Team SkillSharers</p>
      </footer>
    </div>
  );
}

export default Home;
