import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { profileApi } from '../services/api';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    bio: '',
    profilePhoto: '',
    coverPhoto: '',
    linkedin: '',
    github: '',
    workExperience: [],
    projects: [],
    skills: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const loadProfile = async () => {
      try {
        if (!user) {
          navigate('/login');
          return;
        }

        const data = await profileApi.getProfile(user.id);
        if (data) {
          setFormData({
            name: data.name || '',
            bio: data.bio || '',
            profilePhoto: data.profilePhoto || '',
            coverPhoto: data.coverPhoto || '',
            linkedin: data.linkedin || '',
            github: data.github || '',
            workExperience: data.workExperience || [],
            projects: data.projects || [],
            skills: data.skills || []
          });
        }
      } catch (err) {
        console.error('Error loading profile:', err);
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      // Prepare the profile data
      const profileData = {
        ...formData,
        userId: user.id,
        skills: formData.skills.map(skill => ({
          name: skill.name,
          rating: skill.rating || 0
        }))
      };

      // Always use updateProfile since we're updating an existing profile
      await profileApi.updateProfile(user.id, profileData);
      setSuccess(true);

      // Redirect to profile page after successful update
      navigate('/profile');
    } catch (err) {
      console.error('Error updating profile:', err);
      setError('Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleArrayInputChange = (index, field, value, arrayName) => {
    setFormData(prev => ({
      ...prev,
      [arrayName]: prev[arrayName].map((item, i) => 
        i === index ? { ...item, [field]: value } : item
      )
    }));
  };

  const addArrayItem = (arrayName, defaultItem) => {
    setFormData(prev => ({
      ...prev,
      [arrayName]: [...prev[arrayName], defaultItem]
    }));
  };

  const removeArrayItem = (arrayName, index) => {
    setFormData(prev => ({
      ...prev,
      [arrayName]: prev[arrayName].filter((_, i) => i !== index)
    }));
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <h1>Edit Profile</h1>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">Profile updated successfully!</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <h2>Basic Information</h2>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Bio:</label>
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Images</h2>
          <div className="form-group">
            <label>Profile Photo URL:</label>
            <input
              type="url"
              name="profilePhoto"
              value={formData.profilePhoto}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Cover Photo URL:</label>
            <input
              type="url"
              name="coverPhoto"
              value={formData.coverPhoto}
              onChange={handleInputChange}
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Social Links</h2>
          <div className="form-group">
            <label>LinkedIn:</label>
            <input
              type="url"
              name="linkedin"
              value={formData.linkedin}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>GitHub:</label>
            <input
              type="url"
              name="github"
              value={formData.github}
              onChange={handleInputChange}
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Work Experience</h2>
          {formData.workExperience.map((exp, index) => (
            <div key={index} className="array-item">
              <div className="form-group">
                <label>Company:</label>
                <input
                  type="text"
                  value={exp.company}
                  onChange={(e) => handleArrayInputChange(index, 'company', e.target.value, 'workExperience')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Position:</label>
                <input
                  type="text"
                  value={exp.position}
                  onChange={(e) => handleArrayInputChange(index, 'position', e.target.value, 'workExperience')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Duration:</label>
                <input
                  type="text"
                  value={exp.duration}
                  onChange={(e) => handleArrayInputChange(index, 'duration', e.target.value, 'workExperience')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Description:</label>
                <textarea
                  value={exp.description}
                  onChange={(e) => handleArrayInputChange(index, 'description', e.target.value, 'workExperience')}
                  required
                />
              </div>
              <button
                type="button"
                onClick={() => removeArrayItem('workExperience', index)}
                className="remove-btn"
              >
                Remove
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={() => addArrayItem('workExperience', { company: '', position: '', duration: '', description: '' })}
            className="add-btn"
          >
            Add Experience
          </button>
        </div>

        <div className="form-section">
          <h2>Projects</h2>
          {formData.projects.map((project, index) => (
            <div key={index} className="array-item">
              <div className="form-group">
                <label>Title:</label>
                <input
                  type="text"
                  value={project.title}
                  onChange={(e) => handleArrayInputChange(index, 'title', e.target.value, 'projects')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Description:</label>
                <textarea
                  value={project.description}
                  onChange={(e) => handleArrayInputChange(index, 'description', e.target.value, 'projects')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Link:</label>
                <input
                  type="url"
                  value={project.link}
                  onChange={(e) => handleArrayInputChange(index, 'link', e.target.value, 'projects')}
                />
              </div>
              <button
                type="button"
                onClick={() => removeArrayItem('projects', index)}
                className="remove-btn"
              >
                Remove
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={() => addArrayItem('projects', { title: '', description: '', link: '' })}
            className="add-btn"
          >
            Add Project
          </button>
        </div>

        <div className="form-section">
          <h2>Skills</h2>
          {formData.skills.map((skill, index) => (
            <div key={index} className="array-item">
              <div className="form-group">
                <label>Skill Name:</label>
                <input
                  type="text"
                  value={skill.name}
                  onChange={(e) => handleArrayInputChange(index, 'name', e.target.value, 'skills')}
                  required
                />
              </div>
              <div className="form-group">
                <label>Rating (1-5):</label>
                <input
                  type="number"
                  min="1"
                  max="5"
                  value={skill.rating}
                  onChange={(e) => handleArrayInputChange(index, 'rating', parseInt(e.target.value), 'skills')}
                  required
                />
              </div>
              <button
                type="button"
                onClick={() => removeArrayItem('skills', index)}
                className="remove-btn"
              >
                Remove
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={() => addArrayItem('skills', { name: '', rating: 1 })}
            className="add-btn"
          >
            Add Skill
          </button>
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Saving...' : 'Save Profile'}
        </button>
      </form>
    </div>
  );
};

export default AdminDashboard;
