// src/components/Sidebar.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaHome, FaTachometerAlt, FaEnvelope, FaUserCircle } from 'react-icons/fa';
import '../styles/Sidebar.css';

function Sidebar() {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [showProfileBox, setShowProfileBox] = useState(false);

  const toggleSidebar = () => setIsCollapsed(!isCollapsed);
  const toggleProfileBox = () => setShowProfileBox(!showProfileBox);

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-toggle" onClick={toggleSidebar}>
        â˜°
      </div>

      <div className="sidebar-menu">
        <Link to="/">
          <FaHome />
          {!isCollapsed && <span>Home</span>}
        </Link>
        <Link to="/dashboard">
          <FaTachometerAlt />
          {!isCollapsed && <span>Dashboard</span>}
        </Link>
        <Link to="/messages">
          <FaEnvelope />
          {!isCollapsed && <span>Messages</span>}
        </Link>
        <div className="profile-icon" onClick={toggleProfileBox}>
          <FaUserCircle />
        </div>
      </div>

      {showProfileBox && (
        <div className="profile-box">
          <div className="profile-header">
            <img src="https://via.placeholder.com/40" alt="User" className="profile-img" />
            <div className="profile-info">
              <div className="profile-name">John Doe</div>
              <div className="profile-role">Hiring</div>
            </div>
          </div>
          <div className="profile-options">
            <button>Switch to Talent</button>
            <button>Settings</button>
            <button>Help</button>
            <button className="logout-btn">Logout</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Sidebar;
