import React, { useState, useEffect, useRef } from 'react';
import {
  FaHome,
  FaTachometerAlt,
  FaEnvelope,
  FaUserCircle,
  FaBars,
} from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import LogoutConfirmationModal from './LogoutConfirmationModal';
import './Sidebar.css';

function Sidebar({ isCollapsed, toggleSidebar }) {
  const [showProfileBox, setShowProfileBox] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const profileBoxRef = useRef(null); // ðŸ‘ˆ Reference for the profile box
  const navigate = useNavigate();

  const handleLogoutClick = () => {
    setShowLogoutModal(true);
  };

  const confirmLogout = () => {
    setShowLogoutModal(false);
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };

  // Handle clicking outside of profile box
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (profileBoxRef.current && !profileBoxRef.current.contains(event.target)) {
        setShowProfileBox(false); // Close profile box if clicked outside
      }
    };

    // Attach event listener
    document.addEventListener('mousedown', handleClickOutside);

    // Cleanup on component unmount
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []); // Empty dependency array to run only on mount/unmount

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <button onClick={toggleSidebar} className="toggle-btn">
          <FaBars />
        </button>
      </div>

      <div className="profile-wrapper">
        <div
          className="profile-section"
          onClick={() => {
            setShowProfileBox(false);
            navigate('/profile');
          }}
        >
          <FaUserCircle />
          {!isCollapsed && <span>Profile</span>}
        </div>

        {showProfileBox && (
          <div className="profile-box" ref={profileBoxRef}>
            <div className="profile-header">
              <img
                src="https://via.placeholder.com/40"
                alt="User"
                className="profile-img"
                onClick={() => navigate('/profile')} // Redirect to profile page on image click
              />
              <div className="profile-info">
                <div className="profile-name">John Doe</div>
                <div className="profile-role">Hiring</div>
              </div>
            </div>
            <div className="profile-options">
              <button
                onClick={() => {
                  setShowProfileBox(false);
                  navigate('/settings');
                }}
              >
                Settings
              </button>
              <button
                className="btn btn-outline-primary"
                onClick={() => {
                  setShowProfileBox(false);
                  navigate('/contact');
                }}
              >
                Help
              </button>
              <button className="logout-btn" onClick={handleLogoutClick}>
                Logout
              </button>
            </div>
          </div>
        )}
      </div>

      <div className="sidebar-links">
        <Link to="/user">
          <FaHome /> {!isCollapsed && <span>Home</span>}
        </Link>
        <Link to="/admin-dashboard">
          <FaTachometerAlt /> {!isCollapsed && <span>Dashboard</span>}
        </Link>
        <Link to="/MessagingLanding">
          <FaEnvelope /> {!isCollapsed && <span>Messages</span>}
        </Link>
      </div>

      <LogoutConfirmationModal
        show={showLogoutModal}
        onConfirm={confirmLogout}
        onCancel={cancelLogout}
      />
    </div>
  );
}

export default Sidebar;
