// src/pages/Profile.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getProfile } from '../services/api';
import '../styles/Profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        // Get userId from localStorage or your auth context
        const userId = localStorage.getItem('userId');
        if (!userId) {
          throw new Error('User not authenticated');
        }

        // First try to get from localStorage for immediate display
        const cachedData = localStorage.getItem('adminProfile');
        if (cachedData) {
          setProfileData(JSON.parse(cachedData));
        }

        // Then fetch from API to ensure we have the latest data
        const data = await getProfile(userId);
        setProfileData(data);
        // Update localStorage with fresh data
        localStorage.setItem('adminProfile', JSON.stringify(data));
      } catch (err) {
        setError(err.message || 'Failed to load profile');
        console.error('Error loading profile:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  if (loading) {
    return <div className="profile-loading">Loading profile...</div>;
  }

  if (error) {
    return (
      <div className="profile-error">
        <p>{error}</p>
        <button onClick={() => navigate('/dashboard')}>Go to Dashboard</button>
      </div>
    );
  }

  if (!profileData) {
    return (
      <div className="profile-error">
        <p>No profile data found</p>
        <button onClick={() => navigate('/dashboard')}>Create Profile</button>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-banner">
        <img src={profileData.bannerImage || "https://via.placeholder.com/600x200"} alt="Banner" />
      </div>

      <div className="profile-main">
        <div className="profile-photo">
          <img src={profileData.image || "https://via.placeholder.com/100"} alt="Profile" />
        </div>

        <div className="profile-info">
          <h2>{profileData.name}</h2>
          <p className="profile-role">{profileData.positionTitle} at {profileData.companyName}</p>
          <p className="profile-bio">{profileData.bio}</p>

          <div className="profile-section">
            <h4>Contact</h4>
            <p>Email: {profileData.email}</p>
            {profileData.twitter && <p>Twitter: <a href={profileData.twitter} target="_blank" rel="noreferrer">{profileData.twitter}</a></p>}
            {profileData.instagram && <p>Instagram: <a href={profileData.instagram} target="_blank" rel="noreferrer">{profileData.instagram}</a></p>}
          </div>

          <div className="profile-section">
            <h4>About</h4>
            <p>{profileData.fullBio}</p>
          </div>

          <div className="profile-section">
            <h4>Experience</h4>
            <p>{profileData.experienceSummary}</p>
          </div>

          <div className="profile-section">
            <h4>Popular Work</h4>
            <p>{profileData.popularWork}</p>
            {profileData.workLink && <a href={profileData.workLink} target="_blank" rel="noreferrer">View Work</a>}
          </div>

          <div className="profile-section">
            <h4>Project</h4>
            <p>{profileData.projectDescription}</p>
            {profileData.githubLink && <a href={profileData.githubLink} target="_blank" rel="noreferrer">GitHub</a>}
          </div>

          <button 
            className="btn btn-primary mt-3"
            onClick={() => navigate('/dashboard')}
          >
            Edit Profile
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
