import React, { useState } from 'react';
import {
  FaHome,
  FaTachometerAlt,
  FaEnvelope,
  FaUserCircle,
  FaBars,
} from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import LogoutModal from './LogoutModal';
import './Sidebar.css';

function Sidebar({ isCollapsed, toggleSidebar }) {
  const [showProfileBox, setShowProfileBox] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const { setIsLoggedIn } = useAuth();
  const toggleProfileBox = () => setShowProfileBox(!showProfileBox);
  const navigate = useNavigate();

  const handleLogout = () => {
    setShowLogoutModal(false);
    setIsLoggedIn(false);
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <button onClick={toggleSidebar} className="toggle-btn">
          <FaBars />
        </button>
      </div>

      <div className="profile-wrapper">
        <div className="profile-section" onClick={toggleProfileBox}>
          <FaUserCircle />
          {!isCollapsed && <span>Profile</span>}
        </div>

        {showProfileBox && (
          <div className="profile-box">
            <div className="profile-header">
              <img
                src="https://via.placeholder.com/40"
                alt="User"
                className="profile-img"
              />
              <div className="profile-info">
                <div className="profile-name">John Doe</div>
                <div className="profile-role">Hiring</div>
              </div>
            </div>
            <div className="profile-options">
              <button>Switch to Talent</button>
              <button>Settings</button>
              <button
                className="btn btn-outline-primary"
                onClick={() => {
                  setShowProfileBox(false);
                  navigate('/contact');
                }}
              >
                Help
              </button>
              <button
                className="logout-btn"
                onClick={() => {
                  setShowLogoutModal(true);
                }}
              >
                Logout
              </button>
            </div>
          </div>
        )}
      </div>

      <div className="sidebar-links">
        <Link to="/">
          <FaHome /> {!isCollapsed && <span>Home</span>}
        </Link>
        <Link to="/Dashboard">
          <FaTachometerAlt /> {!isCollapsed && <span>Dashboard</span>}
        </Link>
        <Link to="/MessagingLanding">
          <FaEnvelope /> {!isCollapsed && <span>Messages</span>}
        </Link>
      </div>

      <LogoutModal
        show={showLogoutModal}
        onCancel={() => setShowLogoutModal(false)}
        onConfirm={handleLogout}
      />
    </div>
  );
}

export default Sidebar;
