// src/components/Sidebar.jsx
import React, { useState } from 'react';
import { FaHome, FaTachometerAlt, FaEnvelope, FaUserCircle, FaBars } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import '../styles/Sidebar.css';

function Sidebar({ isCollapsed, toggleSidebar }) {
  const [showProfileBox, setShowProfileBox] = useState(false);
  const toggleProfileBox = () => setShowProfileBox(!showProfileBox);

  return (
    <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <button onClick={toggleSidebar} className="toggle-btn">
          <FaBars />
        </button>
      </div>

      <div className="sidebar-links">
        <Link to="/"><FaHome /> {!isCollapsed && <span>Home</span>}</Link>
        <Link to="/dashboard"><FaTachometerAlt /> {!isCollapsed && <span>Dashboard</span>}</Link>
        <Link to="/messages"><FaEnvelope /> {!isCollapsed && <span>Messages</span>}</Link>
      </div>

      <div className="profile-section" onClick={toggleProfileBox}>
        <FaUserCircle />
        {!isCollapsed && <span>Profile</span>}
      </div>

      {showProfileBox && (
        <div className="profile-box">
          <div className="profile-header">
            <img src="https://via.placeholder.com/40" alt="User" className="profile-img" />
            <div className="profile-info">
              <div className="profile-name">John Doe</div>
              <div className="profile-role">Hiring</div>
            </div>
          </div>
          <div className="profile-options">
            <button>Switch to Talent</button>
            <button>Settings</button>
            <button>Help</button>
            <button className="logout-btn">Logout</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Sidebar;
