import React from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Profile.css';

const ProfileDetail = () => {
  const location = useLocation();
  const profile = location.state?.profile;

  if (!profile) return <div className="error">Profile not found.</div>;

  return (
    <div className="profile-detail-container">
      <img src={profile.image} alt={profile.name} className="profile-img-large" />
      <h2>{profile.name}</h2>
      <p>{profile.fullBio}</p>
      <p><a href={profile.workLink} target="_blank" rel="noreferrer">Visit Work</a></p>
      <div className="social-links">
        <a href={profile.twitter} target="_blank" rel="noreferrer">Twitter</a>
        <a href={profile.instagram} target="_blank" rel="noreferrer">Instagram</a>
      </div>
      <div className="work-gallery">
        {profile.workImages?.map((img, idx) => (
          <img key={idx} src={img} alt={`work-${idx}`} />
        ))}
      </div>
    </div>
  );
};

export default ProfileDetail;
