import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ProfileCard.css';

const ProfileCard = ({ profile }) => {
  const navigate = useNavigate();
  const [hoverCard, setHoverCard] = useState(false);
  const [hoverName, setHoverName] = useState(false);

  const handleViewProfile = () => {
    navigate('/profile-detail', { state: { profile } });
  };

  const handleContactClick = (e) => {
    e.stopPropagation();
    alert(`Getting in touch with ${profile.name}...`);
  };

  return (
    <div
      className="profile-card"
      onMouseEnter={() => setHoverCard(true)}
      onMouseLeave={() => {
        setHoverCard(false);
        setHoverName(false);
      }}
      onClick={handleViewProfile}
    >
      <div
        className="banner"
        style={{ backgroundImage: `url(${profile.bannerImage || 'https://via.placeholder.com/300x180'})` }}
      >
        {hoverCard && (
          <div className="bio-overlay">
            {profile.bio || 'No bio available'}
          </div>
        )}
      </div>

      <div className="card-bottom">
        <img src={profile.image} alt={profile.name} className="avatar" />

        <div
          className="name-wrapper"
          onMouseEnter={(e) => {
            e.stopPropagation();
            setHoverName(true);
          }}
          onMouseLeave={() => setHoverName(false)}
        >
          <span className="profile-name">{profile.name}</span>

          {hoverName && (
            <div className="name-hover-box">
              <img src={profile.image} alt={profile.name} className="hover-avatar" />
              <h3>{profile.name}</h3>
              {profile.rating && <p>⭐ {profile.rating}</p>}
              <button onClick={handleContactClick}>Get in Touch</button>
            </div>
          )}
        </div>

        <div className="followers-section">
          <button className="heart-btn">❤️</button>
          <span>{profile.followers ? `${profile.followers} Followers` : 'Be the first follower!'}</span>
        </div>
      </div>
    </div>
  );
};

export default ProfileCard;
