import React, { useState, useEffect } from 'react';
import { FaPaperPlane, FaSearch, FaEllipsisV } from 'react-icons/fa';
import '../styles/MessagingLanding.css';

const MessagingLanding = () => {
  const [conversations, setConversations] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [input, setInput] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulated API call to fetch conversations
    const fetchConversations = async () => {
      try {
        // Replace with actual API call
        const mockConversations = [
          { 
            id: 1, 
            name: 'Rahul Sharma',
            lastMessage: 'Hey there!',
            timestamp: '10:30 AM',
            unread: 2,
            avatar: 'https://randomuser.me/api/portraits/men/1.jpg'
          },
          // Add more mock conversations
        ];
        setConversations(mockConversations);
        setCurrentChatId(mockConversations[0]?.id);
      } catch (error) {
        console.error('Error fetching conversations:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchConversations();
  }, []);

  const currentChat = conversations.find(c => c.id === currentChatId);

  const handleSend = () => {
    if (input.trim() === '') return;

    const updatedConversations = conversations.map(conv => {
      if (conv.id === currentChatId) {
        return {
          ...conv,
          lastMessage: input,
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
          unread: 0
        };
      }
      return conv;
    });

    setConversations(updatedConversations);
    setInput('');
  };

  const filteredConversations = conversations.filter(conv =>
    conv.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="messaging-landing">
      <aside className="chat-sidebar">
        <div className="chat-sidebar-header">
          <h4>Messages</h4>
          <div className="search-container">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search conversations..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="conversations-list">
          {loading ? (
            <div className="loading-state">Loading conversations...</div>
          ) : (
            filteredConversations.map(conv => (
              <div
                key={conv.id}
                className={`conversation-item ${conv.id === currentChatId ? 'active' : ''}`}
                onClick={() => setCurrentChatId(conv.id)}
              >
                <img src={conv.avatar} alt={conv.name} className="conversation-avatar" />
                <div className="conversation-info">
                  <div className="conversation-header">
                    <span className="conversation-name">{conv.name}</span>
                    <span className="conversation-time">{conv.timestamp}</span>
                  </div>
                  <div className="conversation-preview">
                    <span className="last-message">{conv.lastMessage}</span>
                    {conv.unread > 0 && (
                      <span className="unread-badge">{conv.unread}</span>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </aside>

      <section className="chat-section">
        {currentChat ? (
          <>
            <div className="chat-header">
              <div className="chat-header-info">
                <img src={currentChat.avatar} alt={currentChat.name} className="chat-avatar" />
                <h5>{currentChat.name}</h5>
              </div>
              <button className="chat-options">
                <FaEllipsisV />
              </button>
            </div>

            <div className="chat-messages">
              {/* Add message history here */}
            </div>

            <div className="chat-input">
              <input
                type="text"
                placeholder="Type your message..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              />
              <button onClick={handleSend} className="send-button">
                <FaPaperPlane />
              </button>
            </div>
          </>
        ) : (
          <div className="no-chat-selected">
            <p>Select a conversation to start messaging</p>
          </div>
        )}
      </section>
    </div>
  );
};

export default MessagingLanding;
