import React, { useState } from 'react';
import './ProfileCard.css';
import { FaHeart, FaStar } from 'react-icons/fa';

const ProfileCard = ({ profile }) => {
  const [hovered, setHovered] = useState(false);
  const [showPopup, setShowPopup] = useState(false);

  return (
    <div 
      className="profile-card-container" 
      onMouseEnter={() => setHovered(true)} 
      onMouseLeave={() => { setHovered(false); setShowPopup(false); }}
    >
      <div className="banner-img" style={{ backgroundImage: `url(${profile.bannerImage})` }}>
        {hovered && (
          <div className="hover-overlay">
            <p className="popular-work">{profile.popularWork}</p>
            <p className="bio-line">{profile.bio}</p>
          </div>
        )}
      </div>

      <div className="card-footer">
        <div className="left-section">
          <img src={profile.image} alt={profile.name} className="profile-img" />
          <span 
            className="profile-name" 
            onMouseEnter={() => setShowPopup(true)} 
            onMouseLeave={() => setShowPopup(false)}
          >
            {profile.name}
          </span>
          {showPopup && (
            <div className="floating-box">
              <img src={profile.image} alt={profile.name} className="popup-img" />
              <h4>{profile.name}</h4>
              <p>{profile.country}</p>
              <div className="rating"><FaStar /> {profile.rating}</div>
              <button className="contact-btn">Get in touch</button>
            </div>
          )}
        </div>
        <div className="right-section">
          <FaHeart className="like-icon" />
          <span>{profile.followers}</span>
        </div>
      </div>
    </div>
  );
};

export default ProfileCard;
