import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FaTwitter, FaInstagram, FaGithub, FaExternalLinkAlt, FaStar, FaDollarSign } from 'react-icons/fa';
import '../styles/Profile.css';

const ProfileDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { token } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    const loadProfile = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/profiles/${id}`, {
          headers: token ? {
            'Authorization': `Bearer ${token}`
          } : {}
        });
        
        if (response.ok) {
          const data = await response.json();
          setProfile(data);
        } else {
          const errorData = await response.json();
          setError(errorData.message || 'Failed to load profile');
        }
      } catch (err) {
        setError('Error loading profile');
        console.error('Error loading profile:', err);
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [id, token]);

  if (loading) {
    return (
      <div className="profile-loading">
        <div className="spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error || !profile) {
    return (
      <div className="profile-error">
        <h3>{error || 'Profile not found'}</h3>
        <p>The profile you're looking for doesn't exist or was removed.</p>
        <button className="btn btn-primary" onClick={() => navigate('/user')}>
          Back to Users
        </button>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-banner">
        <img src={profile.bannerImage} alt="Banner" />
      </div>

      <div className="profile-main">
        <div className="profile-header">
          <div className="profile-photo">
            <img src={profile.image} alt={profile.name} />
          </div>

          <div className="profile-title">
            <h1>{profile.name}</h1>
            <p className="profile-role">{profile.positionTitle} at {profile.companyName}</p>
            
            <div className="profile-rating">
              <FaStar className="star-icon" />
              <span>{profile.rating || 4.5}</span>
              <span className="price-tag"><FaDollarSign />{profile.price || 85}/hr</span>
            </div>
            
            <div className="profile-actions">
              <button className="btn btn-primary" onClick={() => navigate('/MessagingLanding')}>Message</button>
              <button className="btn btn-outline" onClick={() => navigate('/user')}>
                Back to Users
              </button>
            </div>
          </div>
        </div>

        <div className="profile-content">
          <div className="profile-bio-section">
            <h2>About Me</h2>
            <p className="profile-short-bio">{profile.bio}</p>
            <p className="profile-full-bio">{profile.fullBio}</p>
          </div>

          <div className="profile-grid">
            <div className="profile-card">
              <h3>Contact Information</h3>
              <div className="profile-detail">
                <strong>Email:</strong>
                <p>{profile.email}</p>
              </div>
              
              <div className="profile-socials">
                {profile.twitter && (
                  <a href={profile.twitter} target="_blank" rel="noreferrer" className="social-link">
                    <FaTwitter /> Twitter
                  </a>
                )}
                {profile.instagram && (
                  <a href={profile.instagram} target="_blank" rel="noreferrer" className="social-link">
                    <FaInstagram /> Instagram
                  </a>
                )}
                {profile.githubLink && (
                  <a href={profile.githubLink} target="_blank" rel="noreferrer" className="social-link">
                    <FaGithub /> GitHub
                  </a>
                )}
              </div>
            </div>

            <div className="profile-card">
              <h3>Work Experience</h3>
              <div className="profile-detail">
                <strong>Position:</strong>
                <p>{profile.positionTitle}</p>
              </div>
              <div className="profile-detail">
                <strong>Company:</strong>
                <p>{profile.companyName}</p>
              </div>
              <div className="profile-detail">
                <strong>Experience:</strong>
                <p>{profile.experienceSummary}</p>
              </div>
            </div>
            
            <div className="profile-card">
              <h3>Skills</h3>
              <div className="skills-container">
                {Array.isArray(profile.skills) && profile.skills.map((skill, index) => (
                  <span key={index} className="skill-tag">{skill}</span>
                ))}
              </div>
            </div>

            <div className="profile-card">
              <h3>Popular Work</h3>
              <div className="profile-detail">
                <strong>Project:</strong>
                <p>{profile.popularWork}</p>
              </div>
              <div className="profile-detail">
                <strong>Description:</strong>
                <p>{profile.projectDescription}</p>
              </div>
              
              <div className="profile-links">
                {profile.workLink && (
                  <a href={profile.workLink} target="_blank" rel="noreferrer" className="profile-link">
                    <FaExternalLinkAlt /> View Work
                  </a>
                )}
                {profile.githubLink && (
                  <a href={profile.githubLink} target="_blank" rel="noreferrer" className="profile-link">
                    <FaGithub /> View on GitHub
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileDetail;
