// src/components/LogoutConfirmationModal.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './LogoutConfirmationModal.css';

const LogoutConfirmationModal = ({ show, onConfirm, onCancel }) => {
  const navigate = useNavigate();

  if (!show) return null;

  const handleConfirm = () => {
    onConfirm();
    navigate('/'); // Ensure navigation to home page
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Confirm Logout</h2>
        <p>Are you sure you want to log out?</p>
        <div className="modal-buttons">
          <button onClick={handleConfirm} className="confirm-btn">Yes, Logout</button>
          <button onClick={onCancel} className="cancel-btn">Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default LogoutConfirmationModal;
