import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import ProfileCard from '../components/ProfileCard';
import { profileApi } from '../services/api';
import '../styles/User.css';
import { FaSearch } from 'react-icons/fa';

const User = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [profiles, setProfiles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProfiles = async () => {
      try {
        if (!user) {
          navigate('/login');
          return;
        }

        const data = await profileApi.getAllProfiles();
        // Filter out the current user's profile
        const otherProfiles = data.filter(profile => profile.userId !== user.id);
        setProfiles(otherProfiles);
      } catch (err) {
        console.error('Error loading profiles:', err);
        setError('Failed to load profiles');
      } finally {
        setLoading(false);
      }
    };

    loadProfiles();
  }, [user, navigate]);

  const filteredProfiles = profiles.filter(profile => {
    const searchLower = searchTerm.toLowerCase();
    return (
      profile.name.toLowerCase().includes(searchLower) ||
      profile.bio.toLowerCase().includes(searchLower) ||
      profile.skills.some(skill => skill.name.toLowerCase().includes(searchLower))
    );
  });

  // Custom search container and icon styles
  const searchContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '3rem',
    position: 'relative',
    padding: '0 1rem',
  };

  const searchWrapperStyle = {
    position: 'relative',
    width: '100%',
    maxWidth: '600px',
  };

  const searchIconStyle = {
    position: 'absolute',
    left: '16px',
    top: '50%',
    transform: 'translateY(-50%)',
    color: '#74767E',
    fontSize: '1.1rem',
    zIndex: 10,
  };

  const searchBarStyle = {
    padding: '1rem 1.5rem 1rem 3rem',
    width: '100%',
    border: '2px solid #E4E5E7',
    borderRadius: '30px',
    fontSize: '1rem',
    outline: 'none',
    transition: 'all 0.3s ease',
    backgroundColor: '#ffffff',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading profiles...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button onClick={() => window.location.reload()}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="user-page">
      <div className="search-container">
        <div className="search-box">
          <FaSearch className="search-icon" />
          <input
            type="text"
            placeholder="Search by name, bio, or skills..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <div className="profiles-grid">
        {filteredProfiles.length > 0 ? (
          filteredProfiles.map(profile => (
            <ProfileCard key={profile.id} profile={profile} />
          ))
        ) : (
          <div className="no-results">
            <p>No profiles found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default User;
